#define BOAAA_LEFT_PAREN  (
#define BOAAA_RIGHT_PAREN )
#define BOAAA_EVAL(a) a
#define BOAAA_RETURN(...) NOTHING __VA_ARGS__
#define BOAAA_EVAL_2(a, ...) NOTHING a NOTHING (__VA_ARGS__)
#define BOAAA_EVAL_3(f, ...) NOTHING BOAAA_EVAL(f(__VA_ARGS__))

